import React, { Component } from "react";
import { View, Button, TextInput, StyleSheet } from "react-native";
import DateTimePicker from "@react-native-community/datetimepicker";
import { formatDate } from "./util";
import { addAbsence, getEventById } from "./EventService";

class StudentAbsence extends Component {
  state = {
    id: "",
    title: "",
    date: Date.now(),
    description: "",
    showDatePicker: false,
  };

  componentDidMount() {
    const id = this.props.route.params["id"];
    this.setState({id});
  }

  handleChangeTitle = (value) => {
    this.setState({ title: value });
  };

  handleChangeDescription = (value) => {
    this.setState({ description: value });
  };

  handleDatePress = () => {
    this.setState({ showDatePicker: true });
  };

  handleDatePickerHide = () => {
    this.setState({ showDatePicker: false });
  };

  handleDatePicked = (event, selectedDate) => {
    const currentDate = selectedDate || this.state.date;
    this.setState({ date: currentDate });
    this.handleDatePickerHide();
  };

  render() {
    return (
      <View>
        <TextInput
          style={styles.textInput}
          placeholder="дата на отсъствието"
          value={formatDate(this.state.date)}
          editable={!this.state.showDatePicker}
          onFocus={this.handleDatePress}
        />
        {this.state.showDatePicker && (
          <DateTimePicker
            value={this.state.date}
            mode="datetime"
            is24Hour={true}
            onChange={this.handleDatePicked}
          />
        )}
        <TextInput
          style={styles.textInput}
          placeholder="причина за отсъствието"
          value={this.state.description1}
          onChangeText={this.handleChangeDescription}
        />
        <Button
          title="Добави"
          onPress={() => {
            addAbsence({
              id: this.state.id,
              title: this.state.title,
              date: formatDate(this.state.date),
              description: this.state.description,
            }).then(() => this.props.navigation.goBack());
          }}
        />
      </View>
    );
  }
}
export default StudentAbsence;

const styles = StyleSheet.create({
  textInput: {
    backgroundColor: "white",
    padding: 10,
    marginTop: 10,
    marginBottom: 10,
  },
});
