import moment from 'moment';
import uuid from 'uuid';

const BASE_URL = 'https://notesproject-56e1.restdb.io/rest/notes';
const ABSENCES_URL = "https://notesproject-56e1.restdb.io/rest/absence";
const API_KEY = '62a4a6a21a51777906aff956';

export function getEvents() {
    return fetch(BASE_URL,
        {
            method: 'GET',
            headers: {
                'content-type': 'application/json',
                'x-apikey': API_KEY
            }
        }
    )
        .then(response => response.json())
        .then(events => events.map(item => ({
            ...item,
            date: moment(item.date, "DD/MM/YYYY HH:mm").toDate(),
        })));
}

export function getEventById(id) {
    return fetch(`${BASE_URL}/${id}`,
        {
            method: 'GET',
            headers: {
                'content-type': 'application/json',
                'x-apikey': API_KEY
            }
        }
    )
        .then(response => response.json())
        .then(item => {
            return {
                ...item,
                date: moment(item.date, "DD/MM/YYYY HH:mm").toDate()
            }
        })
        .catch(error => console.error(error));
}


export function addEvent({ title, date, description }) {
    return fetch(BASE_URL,
        {
            method: 'POST',
            headers: {
                'content-type': 'application/json',
                'x-apikey': API_KEY
            },
            body: JSON.stringify({
                title, date, description, id: uuid()
            })
        })
        .then(result => result.json())
        .catch(error => console.error(error));
}

export function editEvent({ id, title, date, description }) {
    return fetch(`${BASE_URL}/${id}`,
        {
            method: 'PUT',
            headers: {
                'content-type': 'application/json',
                'x-apikey': API_KEY
            },
            body: JSON.stringify({
                title, date, description
            })
        })
        .then(result => result.json())
        .catch(error => console.error(error));
}

export function deleteEvent(id) {
    return fetch(`${BASE_URL}/${id}`,
        {
            method: 'DELETE',
            headers: {
                'content-type': 'application/json',
                'x-apikey': API_KEY
            }
        })
        .then(result => result.json())
        .catch(error => console.error(error));
}
export function getAbsenceById({ id }) {
    return fetch(ABSENCES_URL,
        {
            method: 'GET',
            headers: {
                'content-type': 'application/json',
                'x-apikey': API_KEY
            },
        })
        .then(result => result.json())
        .catch(error => console.error(error));
}
export function addAbsence({ studentId, title, date, description }) {
    return fetch(ABSENCES_URL,
        {
            method: 'POST',
            headers: {
                'content-type': 'application/json',
                'x-apikey': API_KEY
            },
            body: JSON.stringify({
                title: title,
                date: date,
                description: description
            })
        })
        .then(result => result.json())
        .catch(error => console.error(error))
        .then(item => {
            fetch(`${BASE_URL}/${studentId}`,
                {
                    method: 'PUT',
                    headers: {
                        'content-type': 'application/json',
                        'x-apikey': API_KEY
                    },
                    body: JSON.stringify({
                        $push: {
                            absences: {
                                [item._id]: [item._id, false]
                            },
                        },
                    })
                })
                .then(result => result.json())
                .catch(error => console.error(error));
        });
}
export function changeAbsence({ studentId, id, excused }) {

    fetch(`${BASE_URL}/${studentId}`,
        {
            method: 'PUT',
            headers: {
                'content-type': 'application/json',
                'x-apikey': API_KEY
            },
            body: JSON.stringify({
                $push: {
                    absences: {
                        [id]: [id, excused]
                    },
                },
            })
        })
        .then(result => result.json())
        .catch(error => console.error(error));

    return fetch(ABSENCES_URL,
        {
            method: 'PUT',
            headers: {
                'content-type': 'application/json',
                'x-apikey': API_KEY
            },
            body: JSON.stringify({
                student: studentId,
                excused: excused
            })
        })
        .then(result => result.json())
        .catch(error => console.error(error));
}
